import {
  Button,
  DatePicker,
  Form,
  Image,
  Input,
  Modal,
  Upload,
  message,
  notification,
} from "antd";
import axios from "axios";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";

const { RangePicker } = DatePicker;

const formItemLayout = {
  labelCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 6,
    },
  },
  wrapperCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 14,
    },
  },
};

export const EditProfileModal = ({
  isModalOpen,
  handleCancel,
  data,
  setData,
}) => {
  const auth = useSelector((state) => state?.auth);
  const user = useSelector((state) => state?.auth?.userDetails);
  const [api, contextHolder] = notification.useNotification();
  const [isLoading, setIsLoading] = useState(false);
  const [fileList, setFileList] = useState([]);
  const [values, setValues] = useState({
    firstName: user?.name?.firstName || "",
    middleName: user?.name?.middleName || "",
    lastName: user?.name?.lastName || "",
  });
  let initialValues = {
    firstName: user?.name?.firstName || "",
    middleName: user?.name?.middleName || "",
    lastName: user?.name?.lastName || "",
  };

  const handleUpload = (info) => {
    let newFileList = [...info.fileList];

    // Limit to 1 file
    newFileList = newFileList.slice(-1);

    // Update fileList state
    setFileList(newFileList);

    // Handle upload status
    if (info.file.status === "done") {
      message.success(`${info.file.name} file uploaded successfully`);
    } else if (info.file.status === "error") {
      message.error(`${info.file.name} file upload failed.`);
    }
  };

  useEffect(() => {
    setValues((prevValues) => ({
      ...prevValues,
      firstName: user?.name?.firstName || "",
      middleName: user?.name?.middleName || "",
      lastName: user?.name?.lastName || "",
    }));
  }, [user]);

  const handleOk = async () => {
    try {
      const userPackageData = await axios({
        method: "POST",
        url: `${process.env.NEXT_PUBLIC_BACKEND_URL}/user-packages/create`,
        headers: {
          Authorization: auth?.userData?.token,
        },
        data: values,
      });
      let newData = data;
      newData.push(userPackageData?.data?.data);
      setData(newData);
      setIsLoading(true);
      handleCancel();
    } catch (error) {
      console.log("error: ", error);
      api.error({
        message: "User Package Creating Error",
        description: `${error?.message}`,
        placement: "bottomLeft",
        duration: 5,
      });
      setIsLoading(true);
      handleCancel();
    }
  };

  const beforeUpload = (file) => {
    const isImage = file.type.startsWith("image/");
    if (!isImage) {
      message.error("You can only upload image files!");
      return false;
    }
    if (fileList.length >= 1) {
      message.warning("You can only upload one file.");
      return false;
    }
    return true;
  };

  return (
    <>
      {contextHolder}
      <Modal
        title="Edit Profile"
        open={isModalOpen}
        onOk={handleOk}
        onCancel={handleCancel}
        maskClosable={false}
        centered
        footer={(_, { OkBtn, CancelBtn }) => (
          <>
            <CancelBtn />
            <Button
              type="primary"
              htmlType="submit"
              onClick={handleOk}
              loading={isLoading}
              disabled={isLoading}
            >
              Submit
            </Button>
          </>
        )}
      >
        <Form
          {...formItemLayout}
          variant="filled"
          initialValues={initialValues}
          style={{
            maxWidth: 600,
          }}
        >
          <div className="grid grid-cols-12">
            <div className="col-span-3 flex items-center justify-end p-2">
              <p>Profile Pic :</p>
            </div>
            <div className="col-span-9 flex items-center mb-4">
              <Upload
                action="data:image/png;base64,"
                listType="picture-card"
                fileList={fileList}
                accept="image/*"
                onChange={handleUpload}
                beforeUpload={beforeUpload}
                openFileDialogOnClick={fileList.length < 1}
                multiple={false}
                showUploadList={{
                  showPreviewIcon: false,
                  showRemoveIcon: true,
                }}
                className="rounded"
              >
                {fileList.length < 1 && (
                  <Image
                    width={100}
                    height={100}
                    src="error"
                    className="w-full"
                    preview={false}
                    fallback="data:image/png;base64,"
                  />
                )}
              </Upload>
            </div>
          </div>
          <Form.Item label="First Name" name="firstName">
            <Input
              onChange={(e) =>
                setValues({
                  ...values,
                  firstName: e.target.value,
                })
              }
            />
          </Form.Item>
          <Form.Item label="Middle Name" name="middleName">
            <Input
              onChange={(e) =>
                setValues({
                  ...values,
                  middleName: e.target.value,
                })
              }
            />
          </Form.Item>
          <Form.Item label="Last Name" name="lastName">
            <Input
              onChange={(e) =>
                setValues({
                  ...values,
                  lastName: e.target.value,
                })
              }
            />
          </Form.Item>
        </Form>
      </Modal>
    </>
  );
};
